var ti = require('./index'),
	appc = require('node-appc'),
	afs = appc.fs,
	path = require('path');

exports.detect = function (projectDir) {
	var sdkVersion = appc.version.format(ti.manifest.version, 1, 3),
		searchPaths = (projectDir ? [projectDir] : []).concat(appc.environ.os.sdkPaths.map(function (p) {
			return afs.resolvePath(p);
		})).map(function (p) {
			return path.join(p, 'modules');
		});
	
	dump(searchPaths);
};

exports.find = function (modules, platform, projectDir, logger) {
	var installed = exports.detect(projectDir, logger);
	
	//
};
